create table captcha_codes (id integer not null auto_increment, code tinyint not null, secret_code tinyint not null, time datetime(6) not null, primary key (id));
create table global_settings (id integer not null auto_increment, code varchar(255) not null, name varchar(255) not null, value varchar(255) not null, primary key (id));
create table post_comments (id integer not null auto_increment, text text not null, time datetime(6) not null, parent_id integer, post_id integer not null, user_id integer not null, primary key (id));
create table post_votes (id integer not null auto_increment, time datetime(6) not null, value tinyint check (value in (-1, 1)) not null, post_id integer not null, user_id integer not null, primary key (id));
create table posts (id integer not null auto_increment, is_active tinyint check (is_active in (0, 1)) not null, moderation_status enum('NEW','ACCEPTED','DECLINED') default 'NEW' not null, text text not null, time datetime(6) not null, title varchar(255) not null, view_count integer not null, moderator_id integer, user_id integer not null, primary key (id));
create table tag2post (id integer not null auto_increment, post_id integer not null, tag_id integer not null, primary key (id));
create table tags (id integer not null auto_increment, name varchar(255) not null, primary key (id));
create table users (id integer not null auto_increment, code varchar(255), email varchar(255) not null, is_moderator tinyint check (is_moderator in (0, 1)) not null, name varchar(255) not null, password varchar(255) not null, photo text, reg_time datetime(6) not null, primary key (id));
